import {StyleSheet, View, Text, TouchableOpacity, ImageBackground, Platform, Modal} from 'react-native';
import React, { useEffect, useState } from 'react';


import * as ImagePicker from 'expo-image-picker';
import soybeanImg from '../assets/soybean.png'

export function Upload({ navigation }){

    const [image, setImage] = useState('');


    useEffect(() => {
        (async () => {
          if (Platform.OS !== 'web') {
            const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
            if (status !== 'granted') {
              alert('Desculpa, precisamos da permissÃ£o para usar essa funÃ§Ã£o ðŸ˜¢');
            }
          }
        })();
      }, []);

      const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
          mediaTypes: ImagePicker.MediaTypeOptions.All,
          allowsEditing: true,
          aspect: [4, 3],
          quality: 1,
        });


        console.log(result);

        if (!result.cancelled) {
            await setImage(result.uri);
        }
      };

    return(
        <ImageBackground source={soybeanImg} style={styles.backgroundImg}>
            <View style={styles.container}>
                <View>
                    {(image!=='')&&
                        <>
                        <TouchableOpacity onPress={pickImage}>
                            <Image 
                                source={{ uri: image }} 
                                style={styles.imageInput} 
                            /> 
                        </TouchableOpacity>
                        <Text style={ styles.imgText }> Clique na foto para alterar</Text>
                        </>
                    }
                    {!image &&
                        <TouchableOpacity style={styles.buttonIcon} onPress={pickImage} >
                            <Text style={styles.buttonText}>Clique para fazer Upload</Text>
                        </TouchableOpacity>
                    }

                </View>
            </View>
        </ImageBackground> 
    );
}



const styles = StyleSheet.create({
    container: {
        flex:1,
        alignItems: 'center',
        justifyContent: 'space-around',
        paddingHorizontal: 20,
        paddingVertical: 200
    },
    button: {
      width: 280,
      height: 50,
      backgroundColor: '#615D4F',
      shadowOpacity: 3,
      borderRadius:15,
      marginBottom:10,
      justifyContent: 'center',
      alignItems: 'center'
    },
    button2: {
        width: 280,
        height: 50,
        backgroundColor: '#615D4F',
        shadowOpacity: 3,
        borderRadius:15,
        justifyContent: 'center',
        alignItems: 'center'
      },
    subTitle: {
      fontWeight:'400',
      fontSize: 18,
      color: 'white'
    },
    backgroundImg:{
        flex: 1,
        resizeMode: "cover",
        justifyContent: "center",
        width: '100%'       
    },
    buttonIcon:{
        width: 280,
        height: 50,
        backgroundColor: '#615D4F',
        shadowOpacity: 3,
        borderRadius:15,
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonText:{
        fontWeight:'600',
        fontSize: 20,
        color: 'white'
    }
  });
